                   var oldValue = $scope.ngModel;
                   // console.info( "updating to ", value );
                   $scope.ngModel = '' + value; // overwrites ngModel value
                   $scope.$apply();
                   if (oldValue !== value) {
                       $rootScope.$broadcast('input.value.changed', [$scope.name, value]);
                   }
